#include <bits/stdc++.h>using namespace std;#define ll long long #define Statixia ios_base::sync_with_stdio(false), cin.tie(nullptr), cout.tie(nullptr); ll my_array[30]; void multiply_by_two() {    for (int i = 0; i < 30; ++i) {        my_array[i] *= 2;    }} void sort_descending() {    std::sort(my_array, my_array + 30, std::greater<ll>());} int solve(){    memset(my_array, 0, sizeof(my_array));    ll n, c, dummy;    cin >> n >> c;    for (int i = 0; i < n; i++)    {        cin >> dummy;        my_array[i] = dummy;    }    sort_descending();    for (size_t i = 0; i < 30; i++)    {        if (my_array[i] && my_array[i] <= c)        {            my_array[i] = 0;            multiply_by_two();            sort_descending();            i = -1;        }        else if (!my_array[i])        {            return i;        }    }} int main(){    Statixia    int tc = 1;    cin >> tc;    while (tc--)    {        cout << solve() << '\n';    }    return 0;}