#include <bits/stdc++.h>using namespace std; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     // powers10[k] = 10^k    vector<unsigned long long> pow10(19, 1);    for (int k = 1; k <= 18; ++k) pow10[k] = pow10[k-1] * 10ULL;     int t;     if(!(cin >> t)) return 0;    while (t--) {        unsigned long long n;         cin >> n;         vector<unsigned long long> xs;        for (int k = 1; k <= 18; ++k) {            unsigned long long denom = pow10[k] + 1ULL;   // 10^k + 1            if (n % denom == 0ULL) {                xs.push_back(n / denom);            }        }         if (xs.empty()) {            cout << 0 << "\n";        } else {            sort(xs.begin(), xs.end()); // مطلوب تصاعديًا            cout << xs.size() << "\n";            for (size_t i = 0; i < xs.size(); ++i) {                if (i) cout << ' ';                cout << xs[i];            }            cout << "\n";        }    }    return 0;}